import React from "react";
import { useState, useRef, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import ReactPlayer from 'react-player/lazy';
import ReactDOM from "react-dom";
import Swal from "sweetalert2";


function RealSwim({playList, index}){
    const [step, setStep] = useState(0);
    const [file, setFile] = useState({});

    const handleStep = (e) => {
        setStep(e.target.result);
    };


    const navigate = useNavigate();
    const videoUpload = e =>{
        const videoType = e.target.file.type.includes('video');

        setFile({
            url : URL.createObjectURL(e.target.files),
            video:videoType,
        });
    };
    const navigateMember = () => {
        navigate("/member");
      };


    const [count, setCount] = useState(0);
    const [delay, setDelay] = useState(1000);
    const [isRunning, setIsRunning] = useState(true);

    useInterval(() => {
        // Your custom logic here
        setCount(count + 1);
    
        // Show alert boxes based on count value
        if (count === 5) {
          Swal.fire("Count is 5", "This is the first alert.", "warning");
        } else if (count === 10) {
          Swal.fire("Count is 10", "This is the second alert.", "info");
        } else if (count === 15) {
          Swal.fire("Count is 15", "This is the third alert.", "success");
        }
      }, isRunning ? delay : null);

    return(
        <>
            <h2>실시간 수영장 화면</h2>
            <img src=""></img>
            <center>
                <div style={{align:"center"}}>
                    <ReactPlayer
                        className='react-player'
                        url= "Videos/cctv.mp4"    // 플레이어 url
                        align = "center"
                        width='800px'         // 플레이어 크기 (가로)
                        height='500px'        // 플레이어 크기 (세로)
                        playing={true}        // 자동 재생 on
                        muted={true}          // 자동 재생 on
                        controls={true}       // 플레이어 컨트롤 노출 여부
                        light={false}         // 플레이어 모드
                        pip={true}            // pip 모드 설정 여부
                        poster={'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/images/BigBuckBunny.jpg'}   // 플레이어 초기 포스터 사진
                        // onEnded={() => handleVideo()}  // 플레이어 끝났을 때 이벤트
                    />
                    {/* <video  width='800px'  height='500px'>
                        <source src="Videos/cctv.mp4" type="video/mp4"></source>
                    </video> */}
                </div>
            </center>
            <br></br>
            <div style={{display: 'flex', justifyContent: 'center', gap: '80px'}}>
                <div style={{display:'inline-block', float:'left'}}>
                    <button style={{color:'white', fontSize: '15px', padding: '12px 24px', backgroundColor:'#45B881',border:"none",borderRadius:'10px'}}><strong>다이빙 금지 방송</strong></button>
                </div>
                <div style={{display:'flex', float:'right'}}>
                    <button style={{color:'white', fontSize: '15px', padding: '12px 24px',marginLeft: '30px', backgroundColor:'#3C76AB', border:"none", borderRadius:'10px' }}><strong>사고 방송 송출</strong></button>
                    <button style={{color:'white', fontSize: '15px', padding: '12px 24px' ,marginLeft: '30px', backgroundColor:'#E02E2E', border:"none", borderRadius:'10px'}}><strong>119 긴급 신고</strong></button>
                    <button style={{color:'white', fontSize: '15px', padding: '12px 24px',marginLeft: '30px', backgroundColor:'#645C30',border:"none",borderRadius:'10px' }} onClick={navigateMember}><strong>사고 발생 알림</strong></button>
                </div>
                <div>

                </div>
            </div> 
        </>
    )
}

export default RealSwim;

function useInterval(callback, delay) {
    const savedCallback = useRef();
  
    // Remember the latest function.
    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);
  
    // Set up the interval.
    useEffect(() => {
      function tick() {
        savedCallback.current();
      }
      if (delay !== null) {
        let id = setInterval(tick, delay);
        return () => clearInterval(id);
      }
    }, [delay]);
  }
